var json = (function () {
    var read = function (obj, params) {
        if (typeof(params) === 'string') {
            params = params.split('.');
        }

        if (params.length === 0) {
            return obj;
        }

        if (params.length > 1) {
            obj = obj[params[0]];
            params.shift();
            return read(obj, params);
        } else {
            obj = obj[params[0]];
            return obj;
        }
    };

    return {
        read: read
    };
})();

module.exports = json;
