function locals (app) {
    var configs = {
        url     : nodame.settings.config.server.url,
        app     : nodame.settings.config.app,
        assets  : nodame.settings.config.assets
    };

    for (var config in configs) {
        app.locals[config] = new Object();

        for (var key in configs[config]) {
            app.locals[config][key] = configs[config][key];
        }
    }
}

module.exports = locals;
