function locals (app) {
    var configs = {
        url     : nodame.config('url'),
        app     : nodame.config('app'),
        assets  : nodame.settings.assets
    };

    for (var config in configs) {
        app.locals[config] = {};

        for (var key in configs[config]) {
            app.locals[config][key] = configs[config][key];
        }
    }
}

module.exports = locals;
