var validate   = require('validate.js');

validate.isDefinedAll = function () {
    if (arguments.length > 0) {
        for (var i in arguments) {
            if (!validate.isDefined(arguments[i])) {
                return false;
            }
        }

        return true;
    }

    return false;
};

validate.isEmptyValue = function (obj, value) {
    if (validate.isEmpty(obj)) {
        return value;
    }

    return obj;
};

validate.isMatch = function (value, regex) {
    var found = value.match(regex);

    return !validate.isEmpty(found);
}

module.exports = validate;
